
public class Test006 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a , b;
		a = 10;
		b =5;
		
		int c,d;
		c = a +b;
		d = a -b;
		System.out.printf("%d+%d 합은  %d 이고 , %d -%d 는 %d\n" ,a,b,c, a,b ,d);
		System.out.println("a + b =" + c +"이고 , a -b 는 "+d +"이다");
		System.out.format("%d+%d 합은  %d 이고 , %d -%d 는 %d\n" ,a,b,c, a,b ,d);
	}

}
